BEGIN

  DECLARE OldNovitaCaricate INT;
  DECLARE OldNovitaPrenotate INT;
  DECLARE QuantitaNovitaToAdd INT;
  
  SELECT PRENOTAZ_NOVITA FROM titoli WHERE CHIAVE = OLD.TITOLO INTO OldNovitaPrenotate;
  SELECT NOVITA_CARICATE FROM titoli WHERE CHIAVE = OLD.TITOLO INTO OldNovitaCaricate;
  
	IF NEW.TITOLO <> OLD.TITOLO THEN
      UPDATE titoli SET QUANTITA_MGZN = (QUANTITA_MGZN - OLD.QUANTITA) 
	  	 WHERE CHIAVE = OLD.TITOLO;
      UPDATE titoli SET QUANTITA_MGZN = (QUANTITA_MGZN + NEW.QUANTITA), POS_MAGAZZINO=NEW.UBICAZIONE 
		 WHERE CHIAVE = NEW.TITOLO;
      INSERT INTO movimenti_magazzino (DATA,TITOLO,QUANTITA,DESCRIZIONE)VALUES(NOW(),NEW.TITOLO,NEW.QUANTITA,'Nuovo ingresso titolo');
      INSERT INTO movimenti_magazzino (DATA,TITOLO,QUANTITA,DESCRIZIONE)VALUES(NOW(),OLD.TITOLO,(-1)*OLD.QUANTITA,'Modifica ingresso titolo');     
   ELSE
	   IF (NEW.QUANTITA <> OLD.QUANTITA) OR (NEW.UBICAZIONE <> OLD.UBICAZIONE OR OLD.UBICAZIONE IS NULL) THEN  
         UPDATE titoli SET POS_MAGAZZINO = NEW.UBICAZIONE,
			                  QUANTITA_MGZN = (QUANTITA_MGZN + (NEW.QUANTITA - OLD.QUANTITA)) 
			 WHERE CHIAVE = OLD.TITOLO;
         INSERT INTO movimenti_magazzino (DATA,TITOLO,QUANTITA,DESCRIZIONE)VALUES(NOW(),OLD.TITOLO,NEW.QUANTITA,'Modifica ingresso titolo');
      END IF;
		
		IF NEW.QUANTITA < OLD.QUANTITA THEN
		     IF NEW.QUANTITA < OldNovitaCaricate THEN
			     IF (OLD.QUANTITA - NEW.QUANTITA) <= OldNovitaCaricate THEN
				     UPDATE titoli SET NOVITA_CARICATE = OldNovitaCaricate - (OLD.QUANTITA - NEW.QUANTITA),PRENOTAZ_NOVITA = OldNovitaPrenotate + (OLD.QUANTITA - NEW.QUANTITA) WHERE CHIAVE = OLD.TITOLO;
				  ELSE UPDATE titoli SET NOVITA_CARICATE = 0, PRENOTAZ_NOVITA = OldNovitaPrenotate + OldNovitaCaricate WHERE CHIAVE = OLD.TITOLO;        
				  END IF;
			  END IF;
		ELSE 		
		  IF OldNovitaPrenotate > 0 THEN
		     IF (NEW.QUANTITA - OLD.QUANTITA) <= OldNovitaPrenotate THEN
		        SET QuantitaNovitaToAdd := (NEW.QUANTITA - OLD.QUANTITA);
		     ELSE SET QuantitaNovitaToAdd := OldNovitaPrenotate;
		     END IF;
		     
			  UPDATE titoli SET PRENOTAZ_NOVITA = PRENOTAZ_NOVITA - QuantitaNovitaToAdd,NOVITA_CARICATE = NOVITA_CARICATE + QuantitaNovitaToAdd WHERE CHIAVE = OLD.TITOLO;
		  END IF;
		
		END IF;
		  
   END IF;

END

