BEGIN
  DECLARE NewKey INT;
  DECLARE KeyMateria INT;
  DECLARE KeyClasse INT;
  DECLARE KeyAdozione INT;
  DECLARE KeyIstituto INT;
  DECLARE KeyCombinazione INT;
  
  SELECT MAX(CHIAVE)
    FROM materie_insegnamento 
	WHERE UPPER(DESCRIZIONE) = DescrizioneMateria 
	 INTO KeyMateria;
  
  IF KeyMateria IS NULL THEN
     UPDATE gen_key SET CHIAVE = CHIAVE + 1;
     SELECT MAX(CHIAVE) FROM gen_key INTO KeyMateria; 
     INSERT INTO materie_insegnamento(CHIAVE,DESCRIZIONE) VALUES (KeyMateria,DescrizioneMateria);
  END IF;
  
  SELECT MAX(CHIAVE) 
    FROM classi_combinazioni
   WHERE UPPER(DESCRIZIONE) = DescrizioneCombinazione
    INTO KeyCombinazione;
    
  IF KeyCombinazione IS NULL THEN
     UPDATE gen_key SET CHIAVE = CHIAVE + 1;
     SELECT MAX(CHIAVE) FROM gen_key INTO KeyCombinazione;
     INSERT INTO classi_combinazioni(CHIAVE,DESCRIZIONE)VALUES(KeyCombinazione,DescrizioneCombinazione);
  END IF;
  
  IF (SELECT count(*) FROM titoli WHERE CODICE_ISBN = CodiceTitolo) > 0 THEN
      UPDATE titoli 
		   SET TITOLO = TitoloTitolo, SOTTOTITOLO = SottotitoloTitolo, AUTORI = AutoriTitolo, EDITORE = EditoreTitolo, VOLUME = VolumeTitolo, PREZZO = PrezzoTitolo
		 WHERE CODICE_ISBN = CodiceTitolo;
  ELSE
      UPDATE gen_key SET CHIAVE = CHIAVE + 1;
      SELECT MAX(CHIAVE) FROM gen_key INTO NewKey; 
      INSERT INTO titoli (CHIAVE,CODICE_ISBN,TITOLO,SOTTOTITOLO,AUTORI,EDITORE,VOLUME,MATERIA,PREZZO) VALUES (NewKey,CodiceTitolo,TitoloTitolo,SottotitoloTitolo,AutoriTitolo,EditoreTitolo,VolumeTitolo,KeyMateria,PrezzoTitolo);
  END IF;
  
  SELECT istituti.CHIAVE 
    FROM istituti 
	WHERE istituti.CODICE = IstitutoTitolo  
	 INTO KeyIstituto;
	 
  IF KeyIstituto IS NOT NULL THEN
     INSERT IGNORE INTO istituti_x_titoli (TITOLO,ISTITUTO) VALUES ((SELECT titoli.CHIAVE FROM titoli WHERE CODICE_ISBN = CodiceTitolo),KeyIstituto); 

	  SELECT MAX(CHIAVE)
	  FROM classi 
	  WHERE UPPER(ANNO) = AnnoTitolo AND UPPER(SEZIONE) = SezioneTitolo AND ISTITUTO = KeyIstituto AND COMBINAZIONE = KeyCombinazione
	  INTO KeyClasse;
	  
	  IF KeyClasse IS NULL THEN
	     UPDATE gen_key 
		  SET CHIAVE = CHIAVE + 1;
	     SELECT MAX(CHIAVE) FROM gen_key INTO KeyClasse; 
	     INSERT INTO classi (CHIAVE,ANNO,SEZIONE,ISTITUTO,COMBINAZIONE) VALUES (KeyClasse,AnnoTitolo,SezioneTitolo,KeyIstituto,KeyCombinazione);
	  END IF;
	  
	  SELECT MAX(CHIAVE)
	  FROM adozioni_titolo
	  WHERE TITOLO = (SELECT titoli.CHIAVE FROM titoli WHERE CODICE_ISBN = CodiceTitolo) AND CLASSE = KeyClasse
	  INTO KeyAdozione;
	  
	  IF KeyAdozione IS NULL THEN
	     UPDATE gen_key 
		  SET CHIAVE = CHIAVE + 1;
	     SELECT MAX(CHIAVE) FROM gen_key INTO KeyAdozione; 
	     INSERT INTO adozioni_titolo (CHIAVE,TITOLO,CLASSE) VALUES (KeyAdozione,(SELECT titoli.CHIAVE FROM titoli WHERE CODICE_ISBN = CodiceTitolo),KeyClasse);
	  END IF;
	  
  END IF;
  
END

