BEGIN

  DECLARE QuantitaMagazzino INT;
  DECLARE QuantitaDaSpedire INT;
  
  SELECT titoli.QUANTITA_MGZN FROM titoli WHERE CHIAVE = ChiaveTitolo INTO QuantitaMagazzino;
  SELECT SUM(QUANTITA) FROM dettaglio_spedizioni WHERE dettaglio_spedizioni.STATO = 'S' AND dettaglio_spedizioni.TITOLO = ChiaveTitolo INTO QuantitaDaSpedire;
  
  IF QuantitaDaSpedire IS NULL THEN
     SET QuantitaDaSpedire = 0;
  END IF;
  IF QuantitaMagazzino - QuantitaDaSpedire > 0 THEN RETURN 1;
  ELSE RETURN 0;
  END IF; 
  
END

