  SELECT SUM(QUANTITA) FROM dettaglio_spedizioni WHERE dettaglio_spedizioni.STATO = 'S' AND dettaglio_spedizioni.TITOLO = ChiaveTitolo INTO QuantitaDaSpedire;
  
  IF QuantitaDaSpedire IS NULL THEN
