BEGIN

	DECLARE CicloFinito INTEGER DEFAULT 0;
	DECLARE ChiaveTitolo INT;
	DECLARE OldQuantita INT;
	DECLARE OldQuantitaVol INT;

	DEClARE CursoreTitoli 
		CURSOR FOR 
			SELECT CHIAVE,QUANTITA_MGZN,QUANTITA_MGZN_VOL FROM titoli WHERE QUANTITA_MGZN <> 0 OR QUANTITA_MGZN_VOL <> 0;

	DECLARE CONTINUE HANDLER 
        FOR NOT FOUND SET CicloFinito = 1;

	OPEN CursoreTitoli;

	GetTitoli: LOOP
		FETCH CursoreTitoli INTO ChiaveTitolo,OldQuantita,OldQuantitaVol;
		IF CicloFinito = 1 THEN 
			LEAVE GetTitoli;
		END IF;
		
	   IF OldQuantita <> 0 AND OldQuantitaVol = 0 THEN
		   UPDATE titoli SET QUANTITA_MGZN = 0  WHERE CHIAVE = ChiaveTitolo;
		   INSERT INTO movimenti_magazzino (DATA,TITOLO,QUANTITA,DESCRIZIONE)VALUES(NOW(),ChiaveTitolo,0,'Quantita azzerata da reset inventario');
      END IF;
	   IF OldQuantita = 0 AND OldQuantitaVol <> 0 THEN
	      UPDATE titoli SET QUANTITA_MGZN_VOL = 0  WHERE CHIAVE = ChiaveTitolo;
	      INSERT INTO movimenti_magazzino_volante (DATA,TITOLO,QUANTITA,DESCRIZIONE)VALUES(NOW(),ChiaveTitolo,0,'Quantita volante azzerata da reset inventario');
	   END IF;
		IF OldQuantita <> 0 AND OldQuantitaVol <> 0 THEN
		   UPDATE titoli SET QUANTITA_MGZN = 0,QUANTITA_MGZN_VOL = 0 WHERE CHIAVE = ChiaveTitolo;
	      INSERT INTO movimenti_magazzino (DATA,TITOLO,QUANTITA,DESCRIZIONE)VALUES(NOW(),ChiaveTitolo,0,'Quantita azzerata da reset inventario');
	      INSERT INTO movimenti_magazzino_volante (DATA,TITOLO,QUANTITA,DESCRIZIONE)VALUES(NOW(),ChiaveTitolo,0,'Quantita volante azzerata da reset inventario');
	   END IF;

	END LOOP GetTitoli;
	
	CLOSE CursoreTitoli;

END

BEGIN
  DECLARE OldNovitaCaricate INT;
  DECLARE OldNovitaPrenotate INT;
  
  SELECT PRENOTAZ_NOVITA FROM titoli WHERE CHIAVE = TitoloCarico INTO OldNovitaPrenotate;
  SELECT NOVITA_CARICATE FROM titoli WHERE CHIAVE = TitoloCarico INTO OldNovitaCaricate;

  DELETE FROM ordini_ingresso WHERE CHIAVE = ChiaveCarico;

  IF QuantitaCarico <= OldNovitaCaricate THEN
     UPDATE titoli SET NOVITA_CARICATE = OldNovitaCaricate - QuantitaCarico,PRENOTAZ_NOVITA = OldNovitaPrenotate + QuantitaCarico WHERE CHIAVE = TitoloCarico;
  ELSE UPDATE titoli SET NOVITA_CARICATE = 0, PRENOTAZ_NOVITA = OldNovitaPrenotate + OldNovitaCaricate WHERE CHIAVE = TitoloCarico;        
  END IF;

END

