  DECLARE QuantitaNovitaToAdd INT;
  
  UPDATE gen_key SET CHIAVE = CHIAVE + 1;  
  SELECT MAX(CHIAVE) FROM gen_key INTO NewKey;
  SELECT PRENOTAZ_NOVITA FROM titoli WHERE CHIAVE = TitoloCarico INTO QuantitaNovita;

