  IF QuantitaNovita > 0 THEN
     IF QuantitaCarico <= QuantitaNovita THEN
        SET QuantitaNovitaToAdd := QuantitaCarico;
     ELSE SET QuantitaNovitaToAdd := QuantitaNovita;
     END IF;
     
