BEGIN
  DECLARE KeyMateria INT;    
  DECLARE MateriaUno INT;
  DECLARE MateriaDue INT;
  DECLARE MateriaTre INT;
  
  SELECT MAX(CHIAVE)
    FROM materie_insegnamento 
	WHERE UPPER(DESCRIZIONE) = MateriaLybro 
	 INTO KeyMateria;
  
  IF KeyMateria IS NULL THEN
     UPDATE gen_key SET CHIAVE = CHIAVE + 1;
     SELECT MAX(CHIAVE) FROM gen_key INTO KeyMateria; 
     INSERT INTO materie_insegnamento(CHIAVE,DESCRIZIONE) VALUES (KeyMateria,MateriaLybro);
  END IF;
  
  SELECT docenti.MATERIA_1,docenti.MATERIA_2,docenti.MATERIA_3 INTO MateriaUno,MateriaDue,MateriaTre FROM docenti WHERE LYBRO_KEY = DocenteLKey;

  IF MateriaUno IS NULL THEN
     SET MateriaUno := 0;
  END IF;

  IF MateriaDue IS NULL THEN
     SET MateriaDue := 0;
  END IF;

  IF MateriaTre IS NULL THEN
     SET MateriaTre := 0;
  END IF;

  IF (KeyMateria <> MateriaUno AND KeyMateria <> MateriaDue AND KeyMateria <> MateriaTre) THEN 
      IF MateriaUno = 0 THEN UPDATE docenti SET MATERIA_1 = KeyMateria WHERE LYBRO_KEY = DocenteLKey;
      ELSEIF MateriaDue = 0 THEN UPDATE docenti SET MATERIA_2 = KeyMateria WHERE LYBRO_KEY = DocenteLKey;
      ELSE UPDATE docenti SET MATERIA_3 = KeyMateria WHERE LYBRO_KEY = DocenteLKey;
      END IF;
  END IF;

END

